mapper.select:
SELECT sub.post_idx, sub.writer, sub.category, sub.title, sub.content, sub.post_file, sub.price, sub.register_date, sub.readcount, sub.status, sub.user_id, sub.commentCount
FROM (
         SELECT p.*, r.user_id, (SELECT COUNT(comment_idx) FROM post_comment c WHERE c.post_idx = p.post_idx) AS commentCount
         FROM post p
                  INNER JOIN regular_user r ON p.writer = r.idx
         WHERE p.category = #{category}

     ) sub
ORDER BY sub.post_idx DESC
    LIMIT #{offset}, #{pageSize}

mapper.update:
update post
set READCOUNT = READCOUNT + 1
where POST_IDX = #{post_idx}

mapper.select:
select writer
from post
where post_idx=#{post_idx}
