mapper.insert:
insert into regular_user (user_id, password, name, jumin, gender, tel, email)
values (#{user_id}, #{password}, #{name},
        #{jumin}, #{gender}, #{tel}, #{email}
        )

insert into regular_user (user_id, password, name, email)
values (#{user_id}, #{password}, #{name}, #{email}
       )

insert into regular_user (user_id, password, name, email
, tel
, jumin
, gender
)
values (#{user_id}, #{password}, #{name}, #{email}
, #{tel}
, #{jumin}
, #{gender}
)

mapper.select:
select * from regular_user
WHERE email = #{email}
AND (user_id NOT LIKE 'g_%' AND email NOT LIKE 'k_%' AND email NOT LIKE 'n_%')

mapper.update:
update regular_user
set password = #{password}
where user_id = #{user_id}

mapper.select:
select * from regular_user

    order by idx
    LIMIT #{offset}, #{pageSize}
